import React, {useEffect, useState} from "react";
import { useParams } from 'react-router-dom';
import TutorialService from "../service/TutorialService";
const TutorialComponent = () => {


    const [id] = useParams();
    const history = useHistory();
    const [currentTutorial, setCurrentTutorial] = useState();
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    //
    function handleTitleChange(event){
        setTitle(event.target.value);
    }

    function handleDescriptionChange(event){
        setDescription(event.target.value);
    }

    useEffect(() => {
        if (id) {
            fetchTutorialById(id);
        }
    }, [id]);


    async function fetchTutorialById(id){
        try{
           const data =  await TutorialService.get(id);
           setCurrentTutorial(data)
           setTitle(data.title);
           setDescription(data.description);
        }catch(error){
            console.log(error);
        }
    }

    async function handleUpdate() {
        try {
            await TutorialService.update(currentTutorial.id, { title, description });
            alert("Tutorial updated successfully!");
        } catch (error) {
            console.log(error);
        }
    }

    async function handleDelete() {
        try {
            await TutorialService.delete(currentTutorial.id);
            alert("Tutorial deleted successfully!");
            history.push("/tutorials");
        } catch (error) {
            console.log(error);
        }
    }


    if (!currentTutorial) {
        return <div>Loading...</div>;
    }

    return(
        <>
            <h2>Tutorial</h2>
            <form className="formSubmit">
                <label className="title" style={{display: "block"}}>Title</label>
                <input placeholder="Enter title" type="text" value={title} onChange={handleTitleChange} readOnly={true}/>
                <label className="description" style={{display: "block", marginTop: "20px"}}>Description</label>
                <input placeholder="Enter description" type="text" value={description} onChange={handleDescriptionChange} readOnly={true}/>
                />
                <h6 style={{marginTop: "20px"}}>Status: <span>Pending</span></h6>
                <br/>
                <button type="submit" className="btn btn-primary" style={{marginTop: "20px", marginRight: "10px"}}
                >Publish
                </button>
                <button type="submit" className="btn btn-danger" style={{marginTop: "20px", marginRight: "10px"}}
                >Delete
                </button>
                <button type="submit" className="btn btn-success" style={{marginTop: "20px"}}
                >Update
                </button>
            </form>
        </>
    )
}

export default TutorialComponent;
