import {useEffect, useState} from "react";

function TutorialListComponent(){
    const [tutorials, setTutorials] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [isFormVisible, setIsFormVisible] = useState(true);
    const [isEdited, setIsEdited] = useState(false);
    const [currentTutorial, setCurrentTutorial] = useState(null);
    const [hoveredTutorial, setHoveredTutorial] = useState(null);
    async function fetchData(){
        const url = "http://localhost:8080/api/tutorials";
        try{
            const response = await fetch(url);
            if (!response.ok){
                console.error("Could not fetch resources");
            }
            const data = await response.json();
            setTutorials(data);
        }catch (error){
            console.log(error);
        }
    }

    function handleInputSearchChange(event){
        setInputValue(event.target.value);
    }

    function handleSearchInput(){

    }

    function handleEditClick(tutorial){
        setIsFormVisible(false);
        setIsEdited(true);
        setCurrentTutorial(tutorial);
    }

    useEffect(() => {
        fetchData();
    }, []);


    return(
        <>
        </>
    )

}

export default TutorialListComponent;
